(3855, 257, '_service_detail_detail_content_0_detail_content1_1_detail_content_title', 'field_62abeb3f60c98'),
(3856, 257, 'service_detail_detail_content_0_detail_content1_1_detail_content_content', '\r\nLike conventional or offline marketing, digital marketing also has several types of promotion. Here are the types of digital marketing that you can apply to your business.'),
